const modalBtn=document.querySelectorAll("[data-modal]"),body=document.body,modalClose=document.querySelectorAll(".modal__close"),modal=document.querySelectorAll(".modal");function closeModal(e){let t=e.querySelector(".modal__content");t.removeAttribute("style"),setTimeout(()=>{e.classList.remove("show"),body.classList.remove("no-scroll")},300)}modalBtn.forEach(e=>{e.addEventListener("click",e=>{let t=e.currentTarget;e=t.getAttribute("data-modal");let o=document.getElementById(e),l=o.querySelector(".modal__content");l.addEventListener("click",e=>{e.stopPropagation()}),o.classList.add("show"),body.classList.add("no-scroll"),setTimeout(function(){l.style.transform="none",l.style.opacity="1"},2)})}),modalClose.forEach(e=>{e.addEventListener("click",e=>{closeModal(e.currentTarget.closest(".modal"))})}),modal.forEach(e=>{e.addEventListener("click",e=>{closeModal(e.target)})});const textArea=document.querySelectorAll("[data-autoresize]");textArea.forEach(e=>{let o=e.offsetHeight;e.addEventListener("input",e=>{let t=e.target;t.style.height=o+"px",t.style.height=t.scrollHeight+"px"})});