const searchForm=document.getElementById("search"),searchBtn=document.getElementById("search-btn"),searchInput=document.getElementById("search-input");searchForm.addEventListener("click",e=>{""==searchInput.value&&e.preventDefault(),setTimeout(()=>{searchForm.style.width="210px",searchForm.style.backgroundColor="#202020",searchInput.style.display="block",searchInput.focus()},150),window.addEventListener("click",e=>{let t=e.target;t.closest(".search")||setTimeout(()=>{searchForm.removeAttribute("style"),searchInput.removeAttribute("style"),searchInput.blur()},150)}),searchBtn.addEventListener("click",e=>{""==searchInput.value?e.preventDefault():e.stopPropagation()})});